import React, { useState } from 'react'
import './components/reservations.css'

const initialAvailableTimes = [
  '17:00',
  '18:00',
  '19:00',
  '20:00',
  '21:00',
  '22:00',
];

const availableTimesReducer = (state, action) => {
  return initialAvailableTimes;
};

const Reservations = () => {
  // Define state variables for form fields
  const [date, setDate] = useState(''); // For date
  const [time, setTime] = useState('17:00'); // For time (default to 17:00)
  const [guests, setGuests] = useState(1); // For number of guests
  const [occasion, setOccasion] = useState('Birthday'); // For occasion
  const [availableTimesState, dispatch] = useReducer(
    availableTimesReducer,
    initialAvailableTimes
  );

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Nice");
    // You can access the form field values using the state variables here
    console.log("Date:", date);
    console.log("Time:", time);
    console.log("Number of Guests:", guests);
    console.log("Occasion:", occasion);
  };

  return (
    <>
      <div className='form-container'>
        <form onSubmit={handleSubmit} className='form'>
          <label className='margin-label' htmlFor="res-date">Choose date</label>
          <input
            type="date"
            id="res-date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
          <label htmlFor="res-time">Choose time</label>
          <select
            id="res-time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
          >
            {availableTimes.map((availableTime, index) => (
              <option key={index} value={availableTime}>
                {availableTime}
              </option>
            ))}
          </select>
          <label htmlFor="guests">Number of guests</label>
          <input
            type="number"
            placeholder="1"
            min="1"
            max="10"
            id="guests"
            value={guests}
            onChange={(e) => setGuests(parseInt(e.target.value))}
          />
          <label htmlFor="occasion">Occasion</label>
          <select
            id="occasion"
            value={occasion}
            onChange={(e) => setOccasion(e.target.value)}
          >
            <option value="Birthday">Birthday</option>
            <option value="Anniversary">Anniversary</option>
          </select>
          <button className='nice' type="submit">Make your Reservation</button>
        </form>
      </div>
    </>
  );
}

export default Reservations;
